const form=$(".question").parsley({errorClass:function(){$(this).removeClass("is-valid").addClass("is-invalid")},successClass:function(){$(this).removeClass("is-invalid").addClass("is-valid")}}),questionWrapper=$(".question__item");questionWrapper.not(".first-step").hide();let formStep=0;function updateFormStep(){questionWrapper.fadeOut(250),$(questionWrapper[formStep]).delay(250).fadeIn()}function validateInput(t){return form.isValid({group:t,force:!0})}$("input[type=radio]").change((function(){validateInput($(this).attr("data-parsley-group"))&&(formStep++,updateFormStep())})),$(".question__btn>.btn").click((function(){const t=$(this).attr("id");validateInput(t)?(formStep++,updateFormStep()):form.validate({group:t,force:!0})}));